notes = ""
mapping = {"A":0, "B":1, "C":3}
total = sum(mapping[char] for char in notes)
print(total)

# PART TWO 

notes2 = ""
mapping2 = {"A":0, "B":1, "C":3, "D":5, "x":0}
pairs = [notes2[i:i+2] for i in range(0,len(notes2),2)]
print(pairs)


mapped_values = [str(mapping2[char]) for char in notes2]
mapped_string = ''.join(mapped_values)
print(mapped_string)


total_sum = 0

for pair in pairs:
    if pair[0] != 'x' and pair[1] != 'x' :
        total_sum += mapping2[pair[0]] + mapping2[pair[1]] + 2
    else:
        total_sum += mapping2[pair[0]] + mapping2[pair[1]]

print(total_sum)

# PART 3

notes3 = ""

groups = [notes3[i:i+3] for i in range(0,len(notes3),3)]

print(groups)

total_sum2 = 0

for group in groups:
    x_count = group.count('x')
    if  x_count == 0:
        total_sum2 += mapping2[group[0]] + mapping2[group[1]] + mapping2[group[2]] + 6
    elif  x_count == 1:
        total_sum2 += mapping2[group[0]] + mapping2[group[1]] + mapping2[group[2]] + 2
    else:
        total_sum2 += mapping2[group[0]] + mapping2[group[1]] + mapping2[group[2]] 

print(total_sum2)